INSERT INTO Codecs (Id, Name, Description) VALUES (1, 'AVC', 'Advanced Video Coding (AVC), also referred to as H.264 or MPEG-4 Part 10, is a video compression standard based on block-oriented, motion-compensated coding.');
INSERT INTO Codecs (Id, Name, Description) VALUES (2, 'AAC', 'Advanced Audio Coding (AAC) is an audio coding standard for lossy digital audio compression. Designed to be the successor of the MP3 format, AAC generally achieves higher sound quality than MP3 encoders at the same bit rate. AAC has been standardized by ISO and IEC as part of the MPEG-2 and MPEG-4 specifications.');
INSERT INTO Codecs (Id, Name, Description) VALUES (3, 'HEVC', 'High Efficiency Video Coding (HEVC), also known as H.265 and MPEG-H Part 2, is a video compression standard designed as part of the MPEG-H project as a successor to the widely used Advanced Video Coding (AVC, H.264, or MPEG-4 Part 10).');
INSERT INTO Codecs (Id, Name, Description) VALUES (4, 'MPEG-4 Visual', 'MPEG-4 Part 2, MPEG-4 Visual (formally ISO/IEC 14496-2) is a video compression format developed by the Moving Picture Experts Group (MPEG). It belongs to the MPEG-4 ISO/IEC standards. It is uses block-wise motion compensation and a discrete cosine transform (DCT), similar to previous standards such as MPEG-1 Part 2 and H.262/MPEG-2 Part 2.');
INSERT INTO Codecs (Id, Name, Description) VALUES (5, 'VC-1', 'SMPTE 421, informally known as VC-1, is a video coding format. Most of it was initially developed as Microsoft''s proprietary video format Windows Media Video 9 in 2003.');
INSERT INTO Codecs (Id, Name, Description) VALUES (6, 'WMA', 'Windows Media Audio (WMA) is a series of audio codecs and their corresponding audio coding formats developed by Microsoft with the composer Stan LePard.');
INSERT INTO Codecs (Id, Name, Description) VALUES (7, 'MPEG Audio', 'MPEG-1 Audio Layer II or MPEG-2 Audio Layer II (MP2, sometimes incorrectly called Musicam or MUSICAM) is a lossy audio compression format defined by ISO/IEC 11172-3 alongside MPEG-1 Audio Layer I and MPEG-1 Audio Layer III (MP3).');
INSERT INTO Codecs (Id, Name, Description) VALUES (8, 'WMV2', 'Windows Media Video 8, a DMO-based codec.');
INSERT INTO Codecs (Id, Name, Description) VALUES (9, 'MPEG Video', 'MPEG series video codec.');
INSERT INTO Codecs (Id, Name, Description) VALUES (10, 'PCM', 'Pulse-code modulation (PCM) is a method used to digitally represent sampled analog signals. It is the standard form of digital audio in computers, compact discs, digital telephony and other digital audio applications. In a PCM stream, the amplitude of the analog signal is sampled regularly at uniform intervals, and each sample is quantized to the nearest value within a range of digital steps.');
INSERT INTO Codecs (Id, Name, Description) VALUES (11, 'xvid', 'Xvid (formerly XviD) is a video codec library following the MPEG-4 video coding standard, specifically MPEG-4 Part 2 Advanced Simple Profile (ASP). It uses ASP features such as b-frames, global and quarter pixel motion compensation, lumi masking, trellis quantization, and H.263, MPEG and custom quantization matrices. ');
INSERT INTO Codecs (Id, Name, Description) VALUES (12, 'AC-3', 'Dolby Digital, originally synonymous with Dolby AC-3, is the name for what has now become a family of audio compression technologies developed by Dolby Laboratories. Formerly named Dolby Stereo Digital until 1995, except for Dolby TrueHD, the audio compression is lossy, based on the modified discrete cosine transform (MDCT) algorithm.');
